import 'package:flutter/material.dart';
import 'package:meta/meta.dart';
import 'dart:convert' show base64;

class MyApp extends StatelessWidget {
  @override
  Widget build(BuildContext context) {
    return new MaterialApp(
      debugShowCheckedModeBanner: false,
      title: 'Splash Slider App',
      theme: new ThemeData.dark(),
      home: new Builder(builder: (BuildContext context) {
        return new Scaffold(
          body: new SplashScreen(
            skipSplashCallback: () => Navigator.of(context).pushReplacement(
                  new MaterialPageRoute(builder: (c) => new MainScreen()),
                ),
          ),
        );
      }),
    );
  }
}

class MainScreen extends StatelessWidget {
  @override
  Widget build(BuildContext context) {
    return new Container(
      color: Colors.white,
    );
  }
}

class SplashScreen extends StatefulWidget {
  final VoidCallback skipSplashCallback;

  SplashScreen({
    @required this.skipSplashCallback,
  });

  @override
  _SplashScreenState createState() => new _SplashScreenState();

  static const splashPages = <SplashData>[
    const SplashData(
      const Color(0xFF0BA9F4),
      "What is a Ledge?",
      "A ledge is you taking a jump to meet someone new! Talk with just one person or a group of people and get a study group together, or plan a fun night out!",
      "",
    ),
    const SplashData(
      const Color(0xFFF12821),
      "Start a Ledge",
      "Press the button in the lower right corner to create a new Ledge!",
      null,
    ),
    const SplashData(
      const Color(0xFF4D6A79),
      "Find Ledges",
      "You must create a public ledge to make it available to all users. Protected Ledges are acailable to friends accepted by you. Private Ledges are only not enabled for public view by default.",
      null,
    ),
    const SplashData(
      const Color(0xFF79BC32),
      "Ledges with Others",
      "Together yet far apart. Chat with those on the other side of town, or in the same building as you. Get to know your neighbor or get to know another one in the neighboring city!",
      null,
    ),
  ];
}

class _SplashScreenState extends State<SplashScreen> {
  final PageController _pageController = new PageController();
  StateSetter _pageIndicatorStateSetter;
  int _currentPage = 0;

  @override
  Widget build(BuildContext context) {
    return new Stack(
      children: <Widget>[
        new PageView.builder(
            controller: _pageController,
            itemCount: SplashScreen.splashPages.length,
            itemBuilder: (BuildContext context, int index) =>
                new SplashPage(data: SplashScreen.splashPages[index]),
            onPageChanged: (pageIndex) {
              _pageIndicatorStateSetter(() {
                _currentPage = pageIndex;
              });
            }),
        new Align(
            alignment: Alignment.bottomCenter,
            child: new Material(
              type: MaterialType.transparency,
              child: new Container(
                height: 64.0,
                decoration: new BoxDecoration(
                  border: new Border(
                      top: BorderSide(color: Colors.white10, width: 2.0)),
                ),
                child: new Row(
                  mainAxisSize: MainAxisSize.max,
                  crossAxisAlignment: CrossAxisAlignment.stretch,
                  children: <Widget>[
                    new InkWell(
                      onTap: widget.skipSplashCallback,
                      child: new Padding(
                        padding: const EdgeInsets.symmetric(
                            vertical: 8.0, horizontal: 32.0),
                        child: new Center(
                          child: new Text(
                            'SKIP',
                            style: const TextStyle(
                              color: Colors.white,
                              fontSize: 16.0,
                              fontWeight: FontWeight.bold,
                            ),
                          ),
                        ),
                      ),
                    ),
                    new Expanded(
                      child: new Center(
                        child: new StatefulBuilder(builder:
                            (BuildContext context, StateSetter setState) {
                          _pageIndicatorStateSetter = setState;
                          return new Row(
                            mainAxisSize: MainAxisSize.min,
                            children: new List.generate(
                              SplashScreen.splashPages.length,
                              (i) => new Container(
                                    width: 12.0,
                                    height: 12.0,
                                    margin: const EdgeInsets.symmetric(
                                        horizontal: 6.0),
                                    decoration: new BoxDecoration(
                                      color: (i == _currentPage)
                                          ? Colors.white
                                          : Colors.white30,
                                      shape: BoxShape.circle,
                                    ),
                                  ),
                            ),
                          );
                        }),
                      ),
                    ),
                    new IconButton(
                      onPressed: _nextPage,
                      padding: const EdgeInsets.symmetric(
                          vertical: 8.0, horizontal: 24.0),
                      icon: new Icon(
                        Icons.keyboard_arrow_right,
                        color: Colors.white,
                        size: 28.0,
                      ),
                    ),
                  ],
                ),
              ),
            )),
      ],
    );
  }

  _nextPage() {
    int page = _pageController.page.round();
    if (page + 1 == SplashScreen.splashPages.length) {
      if (widget.skipSplashCallback != null) {
        widget.skipSplashCallback();
      }
    } else {
      _pageController.nextPage(
          duration: const Duration(milliseconds: 300),
          curve: Curves.fastOutSlowIn);
    }
  }
}

class SplashPage extends StatelessWidget {
  final SplashData data;

  SplashPage({
    @required this.data,
  });

  @override
  Widget build(BuildContext context) {
    return Container(
      color: data.color,
      child: new Padding(
        padding: const EdgeInsets.symmetric(horizontal: 32.0),
        child: new SafeArea(
          child: new Column(
            children: <Widget>[
              new Expanded(
                flex: 3,
                child: new Padding(
                  padding: new EdgeInsets.only(top: 32.0, bottom: 32.0),
                  child: data.imageData != null
                      ? new Image.memory(base64.decode(data.imageData),
                          fit: BoxFit.fill)
                      : new Container(),
                ),
              ),
              new Expanded(
                flex: 1,
                child: new Column(
                  crossAxisAlignment: CrossAxisAlignment.start,
                  children: <Widget>[
                    new Padding(
                      padding: const EdgeInsets.only(bottom: 12.0),
                      child: new Text(
                        data.title,
                        style: new TextStyle(fontSize: 18.0),
                      ),
                    ),
                    new Text(
                      data.content,
                      style: new TextStyle(fontSize: 16.0),
                    ),
                  ],
                ),
              ),
              new SizedBox(height: 72.0),
            ],
          ),
        ),
      ),
    );
  }
}

class SplashData {
  final Color color;
  final String title;
  final String content;
  final String imageData;

  const SplashData(this.color, this.title, this.content, this.imageData);
}
